mapper.select:
/*bulletin.selectBulletinPreNextList*/ SELECT A.BULLETIN_SEQ, CASE WHEN UPPER(#{langCd}) = 'KH' THEN A.TITLE_KH ELSE A.TITLE_EN END AS TITLE, A.VIEW_CNT, FN_GET_BASE_DATE_CHAR(A.REG_DT,#{langCd}) AS REG_DT, A.BULLETIN_ID FROM CPES_BULLETIN A WHERE A.BULLETIN_SEQ IN ( ( SELECT B.BULLETIN_SEQ FROM CPES_BULLETIN B WHERE B.BULLETIN_SEQ < #{condBulletinId} AND B.AGREE_YN = 'Y' AND B.DEL_YN = 'N' AND B.VIEW_YN = 'Y'
LIMIT 1 ), ( SELECT B.BULLETIN_SEQ FROM CPES_BULLETIN B WHERE B.BULLETIN_SEQ > #{condBulletinId} AND B.AGREE_YN = 'Y' AND B.DEL_YN = 'N' AND B.VIEW_YN = 'Y'
LIMIT 1 ) )

/*bulletin.selectCommentListCnt*/
SELECT
    	COUNT(1) AS CNT
FROM CPES_COMMENT A
WHERE A.BULLETIN_SEQ = #{condSeq}
AND A.DEL_YN = 'N'
AND A.DEPTH  = #{condDepth}

/*bulletin.selectCommentChildCnt*/
SELECT
    	COUNT(1) AS CNT
FROM CPES_COMMENT A
WHERE A.BULLETIN_SEQ = #{condSeq}
AND A.UPPER_COMMENT_SEQ = #{condUpperSeq}
AND A.DEL_YN = 'N'
