mapper.select:
/*compny.selectVacancyContinueList*/ SELECT A.VACANCY_SEQ , A.COMPNY_SEQ , A.VACANCY_TITLE , A.VACANCY_STS_CD , FN_GET_COMMON_NM('EMPLOY_FORM_CD', A.EMPLOY_FORM_CD, #{langCd}) AS EMPLOY_FORM_NM , A.RECRUMT_MEMB , A.MIN_SALARY_AMT , A.MAX_SALARY_AMT , A.BKMK_CNT , FN_GET_REMAIN_CNT(A.END_DT) REMAIN_DT , FN_GET_REMAIN_DIV(A.END_DT) REMAIN_DIV , ( SELECT F.ADDR_FULL_NM FROM CPES_VACANCY_LOC F WHERE F.VACANCY_SEQ = A.VACANCY_SEQ LIMIT 1 ) AS ADDR_NM , ( SELECT BKMK_SEQ FROM CPES_BKMK F WHERE F.CATE_CD = 'BCT0000000003' AND F.TABLE_KEY_SEQ = A.VACANCY_SEQ AND F.USER_SEQ = #{condUserSeq} LIMIT 1 ) AS BKMK_SEQ , FN_GET_NEW_YN(A.REG_DT) AS NEW_YN FROM CPES_VACANCY A INNER JOIN CPES_COMPNY B ON A.COMPNY_SEQ = B.COMPNY_SEQ WHERE B.COMPNY_SEQ = #{condSeq}
ORDER BY A.REG_DT DESC
LIMIT #{recordCountPerPage} OFFSET #{firstIndex}
