mapper.sql:
WHERE A.USE_YN = 'Y'
AND A.FAIR_VIEW_YN = 'Y'
AND A.DEL_YN = 'N'

AND A.FAIR_DIV_CD = #{condFairDivCd}


AND LEFT(A.ADDR_CD, 3) = #{condAreaCd}


AND ( A.FAIR_NM_EN LIKE CONCAT('%',#{condText},'%') OR A.FAIR_NM_KH LIKE CONCAT('%',#{condText},'%') )

mapper.select:
/*jobFair.selectJobFairListCnt*/
SELECT COUNT(1) AS CNT
FROM CPES_FAIR_MST A

/*jobFair.selectJobFairByEmployersCnt*/ SELECT COUNT(1) FROM CPES_FAIR_PATCPTN_COMPNY A INNER JOIN CPES_COMPNY B ON A.COMPNY_SEQ = B.COMPNY_SEQ
AND B.USE_YN = 'Y' AND B.DEL_YN = 'N'
AND ( B.COMPNY_NM_KH LIKE CONCAT('%',#{condText},'%') OR B.COMPNY_NM_EN LIKE CONCAT('%',#{condText},'%') )

/*jobFair.selectJobFairByEmployers*/ SELECT B.BKMK_CNT, B.COMPNY_SEQ, FN_GET_COMPNY_NM(B.COMPNY_SEQ, #{langCd}) AS COMPNY_NM, FN_GET_ADDR_NM(SUBSTR(B.ADDR_FULL_CD,1,3), '2', #{langCd}) AS ADDR_NM, FN_GET_ISIC_NM(B.ISIC_CD, #{langCd}) AS ISIC_NM, FN_GET_NEW_YN(B.REG_DT) AS NEW_YN, FN_GET_BKMK_SEQ(#{regUserSeq}, 'BCT0000000001', B.COMPNY_SEQ) AS BKMK_SEQ, FN_GET_FILE_PATH(B.LOGO_FILE_GRP_SEQ) AS FILE_URL FROM CPES_FAIR_PATCPTN_COMPNY A INNER JOIN CPES_COMPNY B ON A.COMPNY_SEQ = B.COMPNY_SEQ
AND B.USE_YN = 'Y' AND B.DEL_YN = 'N'
AND ( B.COMPNY_NM_KH LIKE CONCAT('%',#{condText},'%') OR B.COMPNY_NM_EN LIKE CONCAT('%',#{condText},'%') )
LIMIT #{recordCountPerPage} OFFSET #{firstIndex}

/*jobFair.selectJobFairByVacancysCnt*/ SELECT COUNT(1) FROM CPES_FAIR_PATCPTN_COMPNY_VACANCY A INNER JOIN CPES_VACANCY B ON A.VACANCY_SEQ = B.VACANCY_SEQ INNER JOIN CPES_COMPNY C ON A.COMPNY_SEQ = C.COMPNY_SEQ
AND B.USE_YN = 'Y' AND B.DEL_YN = 'N'
AND B.VACANCY_TITLE LIKE CONCAT('%',#{condText},'%')

/*jobFair.selectJobFairByVacancys*/ SELECT B.VACANCY_SEQ, B.COMPNY_SEQ, B.VACANCY_TITLE, B.EMPLOY_FORM_CD, FN_GET_COMMON_NM('EMPLOY_FORM_CD', B.EMPLOY_FORM_CD, #{langCd}) AS EMPLOY_FORM_NM, B.RECRUMT_MEMB, B.MIN_SALARY_AMT, B.MAX_SALARY_AMT, FN_GET_COMPNY_NM(B.COMPNY_SEQ, #{langCd}) AS COMPNY_NM, B.VACANCY_STS_CD, FN_GET_COMMON_NM('VACANCY_STS_CD', B.VACANCY_STS_CD, #{langCd}) AS VACANCY_STS_NM, FN_GET_REMAIN_CNT(B.END_DT) REMAIN_DT, FN_GET_REMAIN_DIV(B.END_DT) REMAIN_DIV, ( SELECT F.ADDR_FULL_NM FROM CPES_VACANCY_LOC F WHERE F.VACANCY_SEQ = B.VACANCY_SEQ LIMIT 1 ) AS ADDR_NM, ( SELECT COUNT(1) FROM CPES_APPLIC F WHERE F.VACANCY_SEQ = B.VACANCY_SEQ AND F.RESUME_SEQ IN ( SELECT G.RESUME_SEQ FROM CPES_RESUME G WHERE G.USER_SEQ = #{regUserSeq} ) ) AS APPLIC_CNT, ( SELECT G.RESUME_SEQ FROM CPES_RESUME G WHERE G.USER_SEQ = #{regUserSeq} AND G.PRI_RESUME_YN = 'Y' LIMIT 1 ) AS RESUME_SEQ, FN_GET_NEW_YN(B.REG_DT) AS NEW_YN, FN_GET_BKMK_SEQ(#{regUserSeq}, 'BCT0000000003', B.VACANCY_SEQ) AS BKMK_SEQ, FN_GET_FILE_PATH(C.LOGO_FILE_GRP_SEQ) AS FILE_URL FROM CPES_FAIR_PATCPTN_COMPNY_VACANCY A INNER JOIN CPES_VACANCY B ON A.VACANCY_SEQ = B.VACANCY_SEQ INNER JOIN CPES_COMPNY C ON A.COMPNY_SEQ = C.COMPNY_SEQ
AND B.USE_YN = 'Y' AND B.DEL_YN = 'N'
AND B.VACANCY_TITLE LIKE CONCAT('%',#{condText},'%')
LIMIT #{recordCountPerPage} OFFSET #{firstIndex}

/*insttFair.selectJobFairByEduInsttsCnt*/ SELECT COUNT(1) AS CNT FROM CPES_FAIR_PATCPTN_INSTT A INNER JOIN CPES_INSTT B ON A.INSTT_SEQ = B.INSTT_SEQ
AND B.DEL_YN = 'N'
AND ( B.INSTT_NM_KH LIKE CONCAT('%',#{condText},'%') OR B.INSTT_NM_EN LIKE CONCAT('%',#{condText},'%') )

/*insttFair.selectJobFairByEduInstts*/ SELECT B.INSTT_SEQ, FN_GET_INSTT_NM(B.INSTT_SEQ, #{langCd}) AS INSTT_NM, FN_GET_COMMON_NM('INSTT_OWNER_CD', B.INSTT_OWNER_CD, #{langCd}) AS INSTT_OWNER_NM, FN_GET_COMMON_NM('INSTT_TYPE_CD', B.INSTT_TYPE_CD, #{langCd}) AS INSTT_TYPE_NM, B.INSTT_ADDR_DTL AS ADDR_NM, B.INSTT_OFFICE_TEL AS TEL1, B.BKMK_CNT, B.INSTT_OWNER_CD, FN_GET_NEW_YN(A.REG_DT) AS NEW_YN, FN_GET_BKMK_SEQ(#{regUserSeq}, 'BCT0000000010', B.INSTT_SEQ) AS BKMK_SEQ, FN_GET_FILE_PATH(B.INSTT_LOGO_FILE_GRP_SEQ) AS FILE_URL FROM CPES_FAIR_PATCPTN_INSTT A INNER JOIN CPES_INSTT B ON A.INSTT_SEQ = B.INSTT_SEQ
AND B.DEL_YN = 'N'
AND ( B.INSTT_NM_KH LIKE CONCAT('%',#{condText},'%') OR B.INSTT_NM_EN LIKE CONCAT('%',#{condText},'%') )
LIMIT #{recordCountPerPage} OFFSET #{firstIndex}

/*jobFair.selectJobFairApplyListCnt*/
SELECT COUNT(1) AS CNT
  FROM CPES_FAIR_MST A

       	/*jobFair.selectJobFairTermCnt*/
		SELECT COUNT(1) AS CNT
		  FROM CPES_FAIR_MST A
		 WHERE A.FAIR_BGN_DT  <=  #{fairEndDt}
		   AND A.FAIR_END_DT  >=  #{fairBgnDt}
		   AND A.USE_YN = 'Y'
		   AND A.FAIR_VIEW_YN = 'Y'
		   AND A.DEL_YN = 'N'
