mapper.select:
		/*privtMem.selectPrivtMemberPwdChk*/
		SELECT
			COUNT(1) AS CNT
		FROM CPES_USER A
        WHERE A.USER_SEQ = #{userSeq}
        AND A.USER_PWD = #{userPwd}

		/*privtMem.selectPwd*/
		SELECT
			A.USER_PWD
		FROM CPES_USER A
        WHERE A.USER_SEQ = #{userSeq}

/*privtMem.selectMemberChg*/ SELECT A.AGREE_REJECT_REASON , A.CHG_AGREE_STS_CD , FN_GET_COMMON_NM('CHG_AGREE_STS_CD', A.CHG_AGREE_STS_CD, #{langCd}) AS CHG_AGREE_STS_NM , A.CHG_NEXT_CD , A.CHG_PREV_CD , A.CHG_REASON , A.JC_CD , A.JC_USER_SEQ , FN_GET_BASE_DATE_CHAR(A.STS_CHG_DT, #{langCd}) AS STS_CHG_DT , A.USER_CHG_SEQ , A.USER_SEQ FROM CPES_USER_CHG A
WHERE A.USER_SEQ = #{condUserSeq}
WHERE A.USER_CHG_SEQ = #{condSeq}
ORDER BY A.REG_DT DESC LIMIT 1
